import React from 'react';
import styled from 'styled-components';
import Input from '../conponents/Input.js';
import Button from '../conponents/Button.js';

const Wrapper = styled.div`
    width: 500px;
    border: 1px solid ;
    height: 350px;
    margin-left: 470px;
    margin-top: 50px;
    border-radius: .25rem;
`
const Btn_input = styled.div`
    margin-top: 30px;
    margin-left: 8px;
`
const FormTest = ({
    email,
    onChangeEmail,
    onChangePwd,
    pwd,
    phone,
    onChangePhone,
    onSubmit,

}) => (
    <Wrapper>
        <Btn_input>
            <Input
                label = "Email:"
                type="Email"
                onChange = {onChangeEmail}
                placeholder={" your Email"}
                validate={{
                    required: true,
                    email: true
                }}
                isDirty={email.isDirty} 
                value={email.value} 
            />
            <Input
                label = "Password:"
                type="Password"
                onChange = {onChangePwd}
                placeholder={" your Password"}
                validate={{
                    required: true,
                    pwd: true
                }}
                isDirty={pwd.isDirty} 
                value={pwd.value} 
            />
            <Input
                label = "Phone:"
                type="Phone"
                onChange = {onChangePhone}
                placeholder={" your Phone"}
                validate={{
                    required: true,
                    phone: true
                }}
                isDirty={phone.isDirty} 
                value={email.value} 

            />
            <Button 
             name="Resinger"
             onClick = {onSubmit}

            />
        </Btn_input>
    </Wrapper>

)

class From extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            email: {
                value: "",
                isDirty: false,
                valid: false
            },
            pwd: {
                value: "",
                isDirty: false,
                valid: false
            },
            phone : {
                value: "",
                isDirty: false
            }
        }

    }

    onChange = (event, valid, name) => {
        this.setState({
            [name]: {
                ...this.state[name],
                value: event.target.value,
                isDirty: true,
                valid: valid
            }
        })
    }

    onSubmit = () =>  {
        console.log("Resinger Sussion")
    }

render() {
    return(
        <FormTest
        email={{
            ...this.state.email,
            onChange: (e, valid) => this.onChange(e, valid, 'email'),
         }}
        pwd={this.state.pwd}
        phone={this.state.phone}
        onSubmit = {this.onSubmit}
        onChange={e => this.onChange(e,'email')}
        />

    )
}
}

export default From;
